<script lang="ts">
import { Component, Vue, Prop } from 'nuxt-property-decorator'
import { queryRegioes } from '~/graphql/queries/regioes'

@Component({
  name: 'MapaIlhas'
})
export default class MapaIlhas extends Vue {
  @Prop() readonly menu !: boolean
  private active: string = 'cidade-de-sao-tome'
  private regioes: any = []
  private item: any = {
    nome: '',
    texto: ''
  }

  handleScroll(anchorId: string): void {
    if (this.$route.hash) {
      const anchor = document.querySelector(`#${anchorId}`)
      if (anchor) {
        window.scrollTo({
          top: anchor.getBoundingClientRect().top + window.pageYOffset - 100,
          behavior: 'smooth'
        })
      }
    }
  }

  get _menu(){
    if (
      this.menu === undefined ||
      this.menu === null ||
      this.menu === false
    ) {
      return false
    } else {
      return true
    }
  }

  setActive(id:string){
    this.active = id
    for (const x in this.regioes) {
      if (Object.prototype.hasOwnProperty.call(this.regioes, x)) {
        if (this.regioes[x].slug === this.active) {
          this.item.nome = this.regioes[x].nome
          this.item.texto = this.regioes[x].resumo
        }        
      }
    }
  }

  mounted(){
    this.setActive('cidade-de-sao-tome')
  }

  created() {
    this.$apollo.addSmartQuery('regioes', {
      query: queryRegioes(''),
      prefetch: true,
    })
  }
  
}
</script>
